$(document).ready(function(){

  $('div.buttons a').on('click', function(e) {
    e.preventDefault();
  });



  function loadPage(page) {
    $.ajax({
      url: '/list/47232',
      data: {
        page: page,
        language: 'en-US',
        sort_by: ''
      }
    }).error(function() {
      showError('There was a problem.');
    }).done(function(response) {
      var items = $(response).find('#list_page_' + page + ' li');
      if (items) {
        $('#list_page_' + page).html(items);
      }


        var loaded_ids = [];
        $.map(items, function(item) {
          loaded_ids.push($(item).attr('id'));
        });
        attachLoggedInAccountTooltips(loaded_ids, 'Pedator$69', '5a722c21c3a368608d010d0a', 'Add to your favorite list', 'Add to your watchlist', 'Remove from your favorite list', 'Remove from your watchlist');

    });
  }

  createInview($('#list_page_1'));
  var last_page = 1;

  $(".lazy").Lazy({
    getPage: function(element) {
      var page = element.attr('data-page');
      loadPage(page);
      createInview(element);
    }
  });

  function createInview(element) {
    new Waypoint.Inview({
      element: element,
      enter: function(direction) {
        var page = $(this.element).attr('data-page');

        if (page == last_page){
          $('div.pagination').remove();
        }
      }
    });
  }


    attachLoggedInAccountTooltips(["52583b25760ee36aaa99592b", "52577751760ee36aaa53c6c4", "52572201760ee3776a2170e7", "52537f9019c29579401c081e", "52576908760ee36aaa3b3bc7", "52571ede760ee3776a1df73a", "5258ec8d760ee346618b7887", "52570776760ee3776a0323d1", "583867ae9251414239000635", "52570dcc760ee3776a090864"], 'Pedator$69', '5a722c21c3a368608d010d0a', 'Add to your favorite list', 'Add to your watchlist', 'Remove from your favorite list', 'Remove from your watchlist');
    enableLoggedInAccountTooltipActions(null, 'Pedator$69', 'Add to your favorite list', 'Add to your watchlist', 'Remove from your favorite list', 'Remove from your watchlist');

});

div.script:
$(document).ready(function(){

  $('#service_select').on('change', function(e){
    selected_item = $(this).val();
    $('#' + selected_item + '_share').removeClass('hide');
    $("#share_window").data("kendoWindow").center();

    if (selected_item == 'twitter') {
      $('#facebook_share').addClass('hide');
    }

    if (selected_item == 'facebook') {
      $('#twitter_share').addClass('hide');
    }
  });

  $('input.share.submit').on('click', function(e) {
    e.preventDefault();
    var selected_submit_button = $(this);
    selected_submit_button.prop('disabled', true);

    if ($(this).hasClass('twitter')) {
      var selected_service = 'twitter';
    } else if ($(this).hasClass('facebook')) {
      var selected_service = 'facebook';
    }

    $.ajax({
      url: '/share/' + selected_service,
      type: 'POST',
      data: $('#' + selected_service + '_form').serialize()
    }).error(function() {
      showError('There was a problem.');
      selected_submit_button.prop('disabled', false);
    }).done(function(response) {
      selected_submit_button.prop('disabled', false);

      if (response.failure) {
        showError('There was a problem sharing your message.');
      }

      if (response.success) {
        showSuccess('Your message has successfully been shared.');
        $('#share_window').data("kendoWindow").close();
      }
    });
  });

});

$(document).ready(function(){

  $("#list_report_window_report_form input.report.submit").on('click', function(e){
    e.preventDefault();
    var selected_submit_button = $(this);
    selected_submit_button.prop('disabled', true);

    $.ajax({
      url: '/modal/report/new',
      type: 'POST',
      data: $('#list_report_window_report_form').serialize()
    }).error(function() {
      showError('There was a problem.');
      selected_submit_button.prop('disabled', false);
    }).done(function(response) {
      selected_submit_button.prop('disabled', false);

      if (response.failure && response.failure.errors) {
        showError(response.failure.errors, response.failure.title);
      }

      if (response.success) {
        showSuccess('Your report was submitted successfully.');
        $("#list_report_window").data("kendoWindow").close();
      }
    });
  });

});

