          function init() {
            window.addEventListener('scroll', function(e){
              var distanceY = window.pageYOffset || document.documentElement.scrollTop,
                shrinkOn = 60,
                header = document.querySelector("header");
              if (distanceY > shrinkOn) {
                $('header').removeClass("normal").removeClass("regular").addClass("smaller");
              } else {
                if ($('header').hasClass("smaller")) {
                  $('header').addClass("normal").addClass("regular").removeClass("smaller");
                }
              }
            });
          }
          window.onload = init();


        showSuccess = function(message, title) {
          if (title === undefined) {
            title = 'Success';
          }

          showNotification({
            title: title,
            message: message
          }, "success");
        };

        showError = function(message, title) {
          if (title === undefined) {
            title = 'Error';
          }

          showNotification({
            title: title,
            message: message
          }, "error");
        };

        var showNotification = (function() {
          var notification = $("span#notification").kendoNotification({
            stacking: "down",

              position: {
                top: 20,
                left: null,
                bottom: null,
                right: 20
              },

            autoHideAfter: 5000,
            show: function(e) {
              e.element.parent().css({
                zIndex: 99999
              });
            },
            templates: [{
              type: "error",
              template: $("#errorTemplate").html()
            },{
              type: "success",
              template: $("#successTemplate").html()
            }]
          }).data("kendoNotification");

          return function(msg, type) {
            notification.show(msg, type);
          };
