        var seach_redirect_template = "/search{0}?query={1}";

        $("#search_v4").kendoAutoComplete({
          animation: false,
          minLength: 1,
          dataTextField: "title",
          template: kendo.template($("#slim_multi_search_template").html()),
          noDataTemplate: $("#search_no_data_template").html(),
          ignoreCase: false,
          dataSource: {
            type: "json",
            serverFiltering: true,
            transport: {
              read: '/search/multi?language=en-US',
              parameterMap: function() {
                return { query: $("#search_v4").data("kendoAutoComplete").value() };
              }
            },
            schema: {
              data: "results"
            }
          },
          popup: {
            appendTo: $("header")
          },
          filtering: function(e) {
            var filter = e.filter;
            if (filter.value == '') {
              if( ($('#search_v4-list #trending_search_header_template').length) == 0 ) {
                $('#search_v4_listbox').empty();
                $('#search_v4-list').prepend($("#search_v4_headerTemplate").html());
              }
            } else {
              $("#trending_search_header_template").remove();
            }
          },
          select: function(e) {
            var dataItem = this.dataItem(e.item.index());
            switch(dataItem.media_type) {
              case 'movie':
                window.location = kendo.format(seach_redirect_template, '/movie', encodeURIComponent(dataItem.title));
                break;
              case 'tv':
                window.location = kendo.format(seach_redirect_template, '/tv', encodeURIComponent(dataItem.name));
                break;
              case 'person':
                window.location = kendo.format(seach_redirect_template, '/person', encodeURIComponent(dataItem.name));
                break;
              default:
                window.location = kendo.format(seach_redirect_template, '', encodeURIComponent(dataItem));
            }
          }
        });

